mapper.select:
SELECT p.id productId, IFNULL(m.company_name, '') investmentName , p.product_name productName, CASE p.is_independent WHEN '0' THEN '否' WHEN '1' THEN '是' END isIndependent, CASE p.product_status WHEN '0' THEN '募集' WHEN '1' THEN '运行' WHEN '2' THEN '清算' END productStatus, IFNULL(ROUND(netValue.net_accumulative_value,2),'') netValue, CASE WHEN netValue.total_share IS NULL THEN IFNULL(ROUND(netValue.asset_value / netValue.net_asset_value,0),'') ELSE IFNULL(ROUND(netValue.total_share, 0),'') END totalShare, CASE WHEN p.is_calculate_netValue = '0' THEN IFNULL(ROUND(netValue.asset_value, 2), '') ELSE IFNULL(ROUND(invester.invester_equity, 2),'') END assetValue, IFNULL(ROUND(avgFunds.avgFundsUseRatio*100.00, 2),'') avgFundsUseRatio, IFNULL(ROUND(invester.maxFunds*100.00, 2), '') maxFundsUseRatio, IFNULL(p.strategy_list, '') stragegy, IFNULL(ROUND(untilNowYieldRatio.value*100.00, 2),'') yieldRatio, IFNULL(ROUND(oneMonthYieldRatio.value*100.00, 2),'') oneMonthYieldRatio, IFNULL(ROUND(threeMonthYieldRatio.value*100.00, 2),'') threeMonthYieldRatio, IFNULL(ROUND(yearYieldRatio.value*100.00, 2), '') yearYieldRatio, IFNULL(ROUND(sharp.value*100.00, 2), '') sharp, IFNULL(ROUND(maxDrawDown.value*-100.00, 2), '') maxDrawDown, IFNULL(max_drawdown_start_date, '') maxDrawdownStartDate, IFNULL(max_drawdown_end_date, '') maxDrawdownEndDate, IFNULL(p.create_date, '') createDate, IFNULL(p.settlement_date, '') settlementDate, CASE p.is_independent WHEN '0' THEN IFNULL(netDate.minDate,'') ELSE '' END netValueStartDate, CASE p.is_independent WHEN '0' THEN IFNULL(netDate.maxDate,'') ELSE '' END netValueEndDate FROM t_product p LEFT JOIN t_investment_manager m ON p.investment_id = m.id LEFT JOIN (SELECT temp.product_id, temp.init_share FROM (SELECT a.product_id, a.init_share FROM t_product_share_history a GROUP BY a.product_id, a.date DESC) temp GROUP BY temp.product_id) totalShare ON totalShare.product_id = p.id LEFT JOIN (SELECT temp.product_id, temp.invester_equity, MAX(temp.risk_degree) maxFunds FROM (SELECT r.product_id, a.invester_equity, a.risk_degree FROM t_invester_fund a LEFT JOIN t_product_invester_relationship r ON a.invester_id = r.invester_id AND a.broker_id = r.broker_id WHERE r.product_id IS NOT NULL GROUP BY r.product_id, a.date DESC) temp GROUP BY temp.product_id) invester ON invester.product_id = p.id LEFT JOIN (SELECT temp.product_id, temp.date, temp.net_accumulative_value, temp.asset_value, temp.net_asset_value, temp.total_share FROM (SELECT a.product_id, a.date, a.net_accumulative_value, a.asset_value, a.net_asset_value, a.total_share FROM t_product_net_value a GROUP BY a.product_id, a.date DESC) temp GROUP BY temp.product_id) netValue ON netValue.product_id = p.id LEFT JOIN (SELECT r.product_id, AVG(risk_degree) avgFundsUseRatio FROM t_invester_fund f LEFT JOIN t_product_invester_relationship r ON f.invester_id = r.invester_id AND f.broker_id = r.broker_id WHERE product_id IS NOT NULL GROUP BY r.product_id) avgFunds ON avgFunds.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '020032' AND a.period_type = '0') untilNowYieldRatio ON untilNowYieldRatio.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '020032' AND a.period_type = '1') oneMonthYieldRatio ON oneMonthYieldRatio.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '020032' AND a.period_type = '2') threeMonthYieldRatio ON threeMonthYieldRatio.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '030002' AND a.period_type = '0') yearYieldRatio ON yearYieldRatio.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '040006' AND a.period_type = '0') maxDrawDown ON maxDrawDown.product_id = p.id LEFT JOIN (SELECT a.value, a.product_id FROM t_product_period_statistics a WHERE a.KPI = '030007' AND a.period_type = '0') sharp ON sharp.product_id = p.id LEFT JOIN ( SELECT MIN(DATE) minDate, MAX(DATE) maxDate, product_id FROM t_product_net_value GROUP BY product_id )netDate ON netDate.product_id = p.id WHERE p.product_status <> 3 AND is_parent_product = '0' AND (m.company_name NOT LIKE '%测试%' OR m.company_name IS NULL) AND p.product_name NOT LIKE '%测试%' AND m.id IN (SELECT DISTINCT investment_id FROM t_product tp WHERE is_independent = '1' AND investment_id IS NOT NULL)
and p.product_status in
#{id}
and
(p.strategy_list like CONCAT('%', #{item}, '%'))
and m.company_name = #{request.investmentName ,jdbcType=VARCHAR}
and
p.id = #{item}
and
m.id = #{item}
ORDER BY m.company_name DESC,is_independent DESC ,p.product_name
